$(document).on("click",".tag-cloud",function removeTag(){$(this).remove()});$(document).ready(function(){if($("#tag").length>0){addTagBindings("#tag")}if($("#tag-info").length>0){addTagBindings("#tag-info")}if($("#tag-info_phenotype").length>0){addTagBindings("#tag-info_phenotype")}if($("#tag-success").length>0){addTagBindings("#tag-success")}if($("#tag-warning").length>0){addTagBindings("#tag-warning")}if($("#tag-danger").length>0){addTagBindings("#tag-danger")}if($("#tag-inverse").length>0){addTagBindings("#tag-inverse")}});function addTagBindings(a){$(a+" > button").click(function(){addTag(a)});$(a+" > input").keyup(function(b){if(b.keyCode==13){addTag(a)}})}function addTag(d){var a=$(d+" > input").val();var c="";if(d=="#tag"){c="tag-cloud"}if(d.indexOf("#tag-info")==0){c="tag-cloud tag-cloud-info"}if(d=="#tag-success"){c="tag-cloud tag-cloud-success"}if(d=="#tag-warning"){c="tag-cloud tag-cloud-warning"}if(d=="#tag-danger"){c="tag-cloud tag-cloud-danger"}if(d=="#tag-inverse"){c="tag-cloud tag-cloud-inverse"}var b=new Array(" orange"," darkBlue"," aqua"," brilliantBlue"," red");c+=b[parseInt(Math.random()*5)];if(a!=""){$('<li class="'+c+'" >'+a+"</li>").appendTo(d+"_cloud");$(d+" > input").val("")}}function addTag_by_li(a){var b=$("#"+a).html();var e="#tag-info_phenotype";var d="tag-cloud tag-cloud-info";var c=new Array(" orange"," darkBlue"," aqua"," brilliantBlue"," red");d+=c[parseInt(Math.random()*5)];if(b!=""){if($("#tag_"+a).html()==undefined){$('<li class="'+d+'" id="tag_'+a+'">'+b+"</li>").appendTo(e+"_cloud")}}};